body.script:
        class HoloStreamApp {
            constructor() {
                this.state = {
                    view: 'loader', // loader, lobby, meeting
                    media: {
                        audio: true,
                        video: true,
                        screen: false
                    },
                    devices: {
                        audioInput: [],
                        videoInput: []
                    },
                    participants: [],
                    messages: [],
                    isSidebarOpen: false,
                    sidebarType: null, // 'chat', 'participants'
                    localStream: null,
                    screenStream: null
                };

                this.dom = {
                    loader: document.getElementById('loader'),
                    lobby: document.getElementById('lobby-view'),
                    meeting: document.getElementById('meeting-view'),
                    localVideoLobby: document.getElementById('lobby-video-preview'),
                    localVideoRoom: document.getElementById('local-video-room'),
                    screenVideo: document.getElementById('shared-screen'),
                    screenContainer: document.getElementById('screen-share-container'),
                    gridContainer: document.getElementById('grid-container'),
                    filmstrip: document.getElementById('filmstrip'),
                    chatMessages: document.getElementById('chat-messages'),
                    chatInput: document.getElementById('chat-input'),
                    clock: document.getElementById('clock'),
                    notifications: document.getElementById('notifications'),
                    rightSidebar: document.getElementById('right-sidebar'),
                    audioLevel: document.getElementById('lobby-audio-level')
                };

                this.mockNames = ['Sarah Connor', 'Neo', 'Trinity', 'Morpheus', 'Flynn', 'Tron', 'Major Kusanagi', 'Deckard'];
                this.init();
            }

            async init() {
                // Initialize Background Canvas
                this.initBackgroundParticles();

                // Simulate system boot
                setTimeout(() => {
                    this.dom.loader.style.opacity = '0';
                    setTimeout(() => {
                        this.dom.loader.style.display = 'none';
                        this.dom.lobby.classList.remove('hidden');
                        // Small delay to allow display:block to apply before opacity transition
                        requestAnimationFrame(() => {
                            this.dom.lobby.style.opacity = '1';
                        });
                        this.startLobbyCamera();
                    }, 700);
                }, 2500);

                // Start Clock
                setInterval(() => this.updateClock(), 1000);
                this.updateClock();

                // Event Listeners for Chat
                this.dom.chatInput.addEventListener('keypress', (e) => {
                    if (e.key === 'Enter') this.sendMessage();
                });

                this.initSettings();

                // Reaction Button
                const reactionBtn = document.querySelector('button[title="Reactions"]');
                if(reactionBtn) reactionBtn.onclick = () => this.triggerReaction();

                this.simulateNetworkStats();
            }

            simulateNetworkStats() {
                setInterval(() => {
                    // Only update if settings modal is open and network tab is active (optimization)
                    const isVisible = !document.getElementById('settings-modal').classList.contains('hidden') && 
                                      !document.getElementById('tab-network').classList.contains('hidden');

                    if(isVisible) {
                        const latency = 10 + Math.floor(Math.random() * 5);
                        const bandwidth = (1.0 + Math.random() * 0.4).toFixed(1);
                        const packetLoss = (Math.random() * 0.005).toFixed(3);

                        // Find elements by text content is hard, let's assume structure or add IDs in a real app. 
                        // For this demo, I'll just update the bars randomly.
                        const bars = document.querySelectorAll('#tab-network .h-64 > div > div');
                        bars.forEach(bar => {
                            if(Math.random() > 0.5) {
                                const height = 20 + Math.random() * 80;
                                bar.style.height = height + '%';
                            }
                        });
                    }
                }, 1000);
            }

            updateClock() {
                const now = new Date();
                this.dom.clock.innerText = now.toLocaleTimeString('en-US', { hour12: false });
            }

            /* --- MEDIA HANDLING --- */

            async startLobbyCamera() {
                try {
                    const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
                    this.state.localStream = stream;
                    this.dom.localVideoLobby.srcObject = stream;

                    // Hook up audio visualizer for lobby
                    this.initAudioAnalyzer(stream);

                } catch (e) {
                    this.showNotification('Error accessing media devices', 'error');
                    console.error(e);
                }
            }

            initAudioAnalyzer(stream) {
                const audioContext = new (window.AudioContext || window.webkitAudioContext)();
                const analyser = audioContext.createAnalyser();
                const microphone = audioContext.createMediaStreamSource(stream);
                const javascriptNode = audioContext.createScriptProcessor(2048, 1, 1);

                analyser.smoothingTimeConstant = 0.8;
                analyser.fftSize = 1024;

                microphone.connect(analyser);
                analyser.connect(javascriptNode);
                javascriptNode.connect(audioContext.destination);

                javascriptNode.onaudioprocess = () => {
                    const array = new Uint8Array(analyser.frequencyBinCount);
                    analyser.getByteFrequencyData(array);
                    let values = 0;
                    const length = array.length;
                    for (let i = 0; i < length; i++) {
                        values += (array[i]);
                    }
                    const average = values / length;
                    if(this.dom.audioLevel) {
                         this.dom.audioLevel.style.width = Math.min(100, average * 2) + '%';
                    }

                    // If in room, update speaking indicator
                    if (this.state.view === 'meeting' && average > 10) {
                         this.dom.localVideoRoom.parentElement.classList.add('speaking');
                         document.getElementById('audio-indicator').classList.remove('hidden');
                    } else if (this.state.view === 'meeting') {
                         this.dom.localVideoRoom.parentElement.classList.remove('speaking');
                         document.getElementById('audio-indicator').classList.add('hidden');
                    }
                };
            }

            toggleLobbyVideo() {
                this.state.media.video = !this.state.media.video;
                this.state.localStream.getVideoTracks().forEach(track => track.enabled = this.state.media.video);
                const btn = document.getElementById('lobby-video-btn');

                if (this.state.media.video) {
                    btn.classList.remove('bg-red-500', 'text-white');
                    btn.classList.add('bg-cyan-500', 'text-black');
                    btn.innerHTML = '';
                } else {
                    btn.classList.add('bg-red-500', 'text-white');
                    btn.classList.remove('bg-cyan-500', 'text-black');
                    btn.innerHTML = '';
                }
            }

            toggleLobbyAudio() {
                this.state.media.audio = !this.state.media.audio;
                this.state.localStream.getAudioTracks().forEach(track => track.enabled = this.state.media.audio);
                const btn = document.getElementById('lobby-audio-btn');

                if (this.state.media.audio) {
                    btn.classList.remove('bg-red-500');
                    btn.classList.add('bg-slate-800');
                    btn.innerHTML = '';
                } else {
                    btn.classList.add('bg-red-500');
                    btn.classList.remove('bg-slate-800');
                    btn.innerHTML = '';
                }
            }

            /* --- MEETING LOGIC --- */

            createMeeting() {
                this.enterMeetingRoom();
                this.showNotification('Creating secure frequency...', 'info');
                setTimeout(() => {
                    this.showNotification('Connected to HoloNet', 'success');
                    this.simulateParticipantsJoining();
                }, 1000);
            }

            joinMeeting() {
                const code = document.getElementById('join-code').value;
                if(code.length < 3) {
                    this.showNotification('Invalid Frequency Code', 'error');
                    return;
                }
                this.enterMeetingRoom();
                this.showNotification(`Joining Frequency: ${code}`, 'info');
                setTimeout(() => {
                    this.showNotification('Encryption Keys Exchanged', 'success');
                    this.simulateParticipantsJoining();
                }, 1500);
            }

            enterMeetingRoom() {
                this.state.view = 'meeting';
                this.dom.lobby.style.display = 'none';
                this.dom.meeting.classList.remove('hidden');

                // Transfer stream
                if (this.state.localStream) {
                    this.dom.localVideoRoom.srcObject = this.state.localStream;
                }

                // Sync buttons
                this.updateControlButtons();
            }

            leaveMeeting() {
                if(confirm("Terminate Neural Link?")) {
                    location.reload();
                }
            }

            updateControlButtons() {
                const vBtn = document.getElementById('btn-video');
                const aBtn = document.getElementById('btn-audio');

                if (this.state.media.video) {
                    vBtn.classList.remove('bg-red-500');
                    vBtn.classList.add('bg-slate-700');
                    vBtn.innerHTML = '';
                } else {
                    vBtn.classList.add('bg-red-500');
                    vBtn.classList.remove('bg-slate-700');
                    vBtn.innerHTML = '';
                }

                if (this.state.media.audio) {
                    aBtn.classList.remove('bg-red-500');
                    aBtn.classList.add('bg-slate-700');
                    aBtn.innerHTML = '';
                } else {
                    aBtn.classList.add('bg-red-500');
                    aBtn.classList.remove('bg-slate-700');
                    aBtn.innerHTML = '';
                }
            }

            toggleVideo() {
                this.state.media.video = !this.state.media.video;
                if(this.state.localStream) {
                    this.state.localStream.getVideoTracks().forEach(t => t.enabled = this.state.media.video);
                }
                this.updateControlButtons();
            }

            toggleAudio() {
                this.state.media.audio = !this.state.media.audio;
                if(this.state.localStream) {
                    this.state.localStream.getAudioTracks().forEach(t => t.enabled = this.state.media.audio);
                }
                this.updateControlButtons();
            }

            async startScreenShare() {
                if (this.state.media.screen) {
                    // Stop sharing
                    this.stopScreenShare();
                    return;
                }

                try {
                    const stream = await navigator.mediaDevices.getDisplayMedia({ video: true });
                    this.state.screenStream = stream;
                    this.dom.screenVideo.srcObject = stream;
                    this.state.media.screen = true;

                    stream.getVideoTracks()[0].onended = () => {
                        this.stopScreenShare();
                    };

                    // UI Updates
                    this.dom.screenContainer.classList.remove('hidden');
                    this.dom.gridContainer.classList.add('hidden');
                    document.getElementById('btn-share').classList.add('bg-red-500', 'animate-pulse');
                    document.getElementById('btn-share').classList.remove('bg-cyan-600');

                    this.showNotification('Screen Transmission Active', 'success');

                    // Move participants to filmstrip
                    this.renderFilmstrip();

                } catch (e) {
                    console.error("Screen share cancelled", e);
                }
            }

            stopScreenShare() {
                if(this.state.screenStream) {
                    this.state.screenStream.getTracks().forEach(t => t.stop());
                }
                this.state.screenStream = null;
                this.state.media.screen = false;

                this.dom.screenVideo.srcObject = null;
                this.dom.screenContainer.classList.add('hidden');
                this.dom.gridContainer.classList.remove('hidden');

                document.getElementById('btn-share').classList.remove('bg-red-500', 'animate-pulse');
                document.getElementById('btn-share').classList.add('bg-cyan-600');

                this.showNotification('Screen Transmission Ended', 'info');
            }

            /* --- SIDEBAR & CHAT --- */

            toggleSidebar(type) {
                const sidebar = this.dom.rightSidebar;
                const chatContainer = document.getElementById('sidebar-chat-container');
                const partsContainer = document.getElementById('sidebar-participants-container');
                const title = document.getElementById('sidebar-title');

                if (type === null) {
                    sidebar.classList.add('translate-x-full');
                    setTimeout(() => sidebar.classList.add('hidden'), 300);
                    this.state.isSidebarOpen = false;
                    this.state.sidebarType = null;
                    return;
                }

                // Switch Content
                if (type === 'chat') {
                    chatContainer.classList.remove('hidden');
                    partsContainer.classList.add('hidden');
                    title.innerText = "COMM_LINK";
                } else if (type === 'participants') {
                    chatContainer.classList.add('hidden');
                    partsContainer.classList.remove('hidden');
                    title.innerText = "NEURAL NODES";
                    this.renderParticipantList();
                }

                if (this.state.isSidebarOpen && this.state.sidebarType === type) {
                    this.toggleSidebar(null);
                } else {
                    sidebar.classList.remove('hidden');
                    requestAnimationFrame(() => sidebar.classList.remove('translate-x-full'));
                    this.state.isSidebarOpen = true;
                    this.state.sidebarType = type;
                }
            }

            renderParticipantList() {
                const list = document.getElementById('participants-list');
                list.innerHTML = '';

                // Add Self
                const selfItem = this.createParticipantListItem({name: 'You (Host)', id: 'local'}, true);
                list.appendChild(selfItem);

                // Add Others
                this.state.participants.forEach(p => {
                    list.appendChild(this.createParticipantListItem(p, false));
                });
            }

            createParticipantListItem(p, isSelf) {
                const div = document.createElement('div');
                div.className = "flex items-center justify-between p-3 rounded hover:bg-white/5 transition-colors group cursor-pointer";
                div.innerHTML = `











${p.name}

${isSelf ? 'Host' : 'Connected'}







                `;
                return div;
            }

            sendMessage() {
                const text = this.dom.chatInput.value;
                if (!text.trim()) return;

                this.addChatMessage('You', text, true);
                this.dom.chatInput.value = '';

                // Simulate reply
                setTimeout(() => {
                    const randomParticipant = this.state.participants[Math.floor(Math.random() * this.state.participants.length)] || {name: 'System'};
                    const replies = ["Acknowledged.", "Copy that.", "Uploading data now...", "Can you see my screen?", "Let's pivot to the next topic.", "Packet loss detected."];
                    this.addChatMessage(randomParticipant.name, replies[Math.floor(Math.random() * replies.length)], false);
                }, 2000 + Math.random() * 3000);
            }

            addChatMessage(sender, text, isMe) {
                const div = document.createElement('div');
                div.className = `flex flex-col gap-1 ${isMe ? 'items-end' : 'items-start'}`;

                const bubbleColor = isMe ? 'bg-cyan-500/20 border-cyan-500/30 text-cyan-100' : 'bg-slate-800 border-white/10 text-gray-300';

                div.innerHTML = `


                        ${sender}
                        ${new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}


${text}
                `;

                this.dom.chatMessages.appendChild(div);
                this.dom.chatMessages.scrollTop = this.dom.chatMessages.scrollHeight;
            }

            /* --- PARTICIPANTS SIMULATION --- */

            simulateParticipantsJoining() {
                let count = 0;
                const max = 5;
                const interval = setInterval(() => {
                    if (count >= max) {
                        clearInterval(interval);
                        return;
                    }
                    const name = this.mockNames[count];
                    this.addParticipant(name, count);
                    this.showNotification(`${name} connected`, 'success');
                    count++;
                }, 2000);
            }

            addParticipant(name, index) {
                const id = `p-${index}`;
                this.state.participants.push({ id, name });

                // Add to Grid
                const wrapper = document.createElement('div');
                wrapper.className = "relative bg-slate-800 rounded-xl overflow-hidden border border-white/5 group";
                wrapper.innerHTML = `


${name}






                `;

                this.dom.gridContainer.appendChild(wrapper);

                // Simulate speaking randomly
                setInterval(() => {
                    if(Math.random() > 0.7) {
                        wrapper.querySelector('.speaking-ring').classList.remove('opacity-0');
                        setTimeout(() => {
                             wrapper.querySelector('.speaking-ring').classList.add('opacity-0');
                        }, 2000);
                    }
                }, 5000 + Math.random() * 5000);
            }

            renderFilmstrip() {
                this.dom.filmstrip.innerHTML = '';
                // Add self
                const selfDiv = document.createElement('div');
                selfDiv.className = "min-w-[120px] h-full bg-slate-800 rounded-lg border border-white/10 overflow-hidden relative";
                selfDiv.innerHTML = ``;
                // Re-attach stream
                const vid = selfDiv.querySelector('video');
                vid.srcObject = this.state.localStream;
                this.dom.filmstrip.appendChild(selfDiv);

                // Add others
                this.state.participants.forEach((p, idx) => {
                     const div = document.createElement('div');
                     div.className = "min-w-[120px] h-full bg-slate-800 rounded-lg border border-white/10 overflow-hidden relative";
                     div.innerHTML = `


${p.name}
                     `;
                     this.dom.filmstrip.appendChild(div);
                });
            }

            /* --- UTILS --- */

            showNotification(text, type = 'info') {
                const div = document.createElement('div');
                const colors = {
                    info: 'border-cyan-500 bg-cyan-950/80 text-cyan-400',
                    success: 'border-green-500 bg-green-950/80 text-green-400',
                    error: 'border-red-500 bg-red-950/80 text-red-400'
                };

                div.className = `px-4 py-3 rounded border-l-4 backdrop-blur-md shadow-lg transform translate-x-10 opacity-0 transition-all duration-300 font-mono text-sm ${colors[type]}`;
                div.innerText = text;

                this.dom.notifications.appendChild(div);

                // Animate in
                requestAnimationFrame(() => {
                    div.classList.remove('translate-x-10', 'opacity-0');
                });

                // Remove after 3s
                setTimeout(() => {
                    div.classList.add('translate-x-10', 'opacity-0');
                    setTimeout(() => div.remove(), 300);
                }, 3000);
            }

            initBackgroundParticles() {
                const canvas = document.getElementById('bg-canvas');
                const ctx = canvas.getContext('2d');
                let width, height;
                let particles = [];

                const resize = () => {
                    width = canvas.width = window.innerWidth;
                    height = canvas.height = window.innerHeight;
                };

                window.addEventListener('resize', resize);
                resize();

                class Particle {
                    constructor() {
                        this.x = Math.random() * width;
                        this.y = Math.random() * height;
                        this.vx = (Math.random() - 0.5) * 0.5;
                        this.vy = (Math.random() - 0.5) * 0.5;
                        this.size = Math.random() * 2;
                        this.color = Math.random() > 0.5 ? 'rgba(0, 243, 255, 0.3)' : 'rgba(188, 19, 254, 0.3)';
                    }
                    update() {
                        this.x += this.vx;
                        this.y += this.vy;
                        if(this.x < 0) this.x = width;
                        if(this.x > width) this.x = 0;
                        if(this.y < 0) this.y = height;
                        if(this.y > height) this.y = 0;
                    }
                    draw() {
                        ctx.fillStyle = this.color;
                        ctx.beginPath();
                        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
                        ctx.fill();
                    }
                }

                for(let i=0; i<100; i++) particles.push(new Particle());

                const animate = () => {
                    ctx.clearRect(0, 0, width, height);
                    particles.forEach(p => {
                        p.update();
                        p.draw();
                    });

                    // Connect particles
                    ctx.strokeStyle = 'rgba(255, 255, 255, 0.03)';
                    for(let i=0; i<particles.length; i++) {
                        for(let j=i; j<particles.length; j++) {
                            const dx = particles[i].x - particles[j].x;
                            const dy = particles[i].y - particles[j].y;
                            const dist = Math.sqrt(dx*dx + dy*dy);
                            if(dist < 100) {
                                ctx.beginPath();
                                ctx.moveTo(particles[i].x, particles[i].y);
                                ctx.lineTo(particles[j].x, particles[j].y);
                                ctx.stroke();
                            }
                        }
                    }
                    requestAnimationFrame(animate);
                };
                animate();
            }

            toggleWhiteboard() {
                const wb = document.getElementById('whiteboard-container');
                if(wb.classList.contains('hidden')) {
                    wb.classList.remove('hidden');
                    this.initWhiteboard();
                } else {
                    wb.classList.add('hidden');
                }
            }

            initWhiteboard() {
                if(this.whiteboardInitialized) return;
                this.whiteboardInitialized = true;

                const canvas = document.getElementById('whiteboard-canvas');
                const ctx = canvas.getContext('2d');
                let painting = false;

                // Set size
                const rect = canvas.parentElement.getBoundingClientRect();
                canvas.width = rect.width;
                canvas.height = rect.height;

                // Handle resize
                window.addEventListener('resize', () => {
                    const rect = canvas.parentElement.getBoundingClientRect();
                    // Save content? For now just reset on resize is easier
                    canvas.width = rect.width;
                    canvas.height = rect.height;
                });

                const startPosition = (e) => {
                    painting = true;
                    draw(e);
                }
                const endPosition = () => {
                    painting = false;
                    ctx.beginPath();
                }
                const draw = (e) => {
                    if (!painting) return;
                    const rect = canvas.getBoundingClientRect();
                    const x = e.clientX - rect.left;
                    const y = e.clientY - rect.top;

                    ctx.lineWidth = 3;
                    ctx.lineCap = 'round';
                    ctx.strokeStyle = '#22d3ee'; // Cyan

                    ctx.lineTo(x, y);
                    ctx.stroke();
                    ctx.beginPath();
                    ctx.moveTo(x, y);
                }

                canvas.addEventListener('mousedown', startPosition);
                canvas.addEventListener('mouseup', endPosition);
                canvas.addEventListener('mousemove', draw);
            }

            initSettings() {
                // Tab Switching
                const tabs = document.querySelectorAll('[data-tab]');
                tabs.forEach(tab => {
                    tab.addEventListener('click', () => {
                        // Reset all tabs
                        tabs.forEach(t => t.classList.remove('bg-cyan-900/20', 'text-cyan-400', 'border-cyan-500/30'));
                        tabs.forEach(t => t.classList.add('text-gray-400', 'hover:text-white'));

                        // Activate clicked
                        tab.classList.remove('text-gray-400', 'hover:text-white');
                        tab.classList.add('bg-cyan-900/20', 'text-cyan-400', 'border', 'border-cyan-500/30');

                        // Show Content
                        const targetId = 'tab-' + tab.dataset.tab;
                        document.querySelectorAll('#settings-modal > div > div > div[id^="tab-"]').forEach(content => {
                            content.classList.add('hidden');
                        });
                        document.getElementById(targetId).classList.remove('hidden');
                    });
                });

                // Wire up Settings Button
                const settingsBtn = document.querySelector('button[title="Settings"]');
                if(settingsBtn) {
                    settingsBtn.onclick = () => {
                        document.getElementById('modal-overlay').classList.remove('hidden');
                        document.getElementById('settings-modal').classList.remove('hidden');
                    };
                }
            }

            triggerReaction() {
                const reactions = ['', '', '', '', '', ''];
                const reaction = reactions[Math.floor(Math.random() * reactions.length)];
                const container = this.dom.meeting; // or stage

                // Create multiple floating elements
                for(let i=0; i<5; i++) {
                    setTimeout(() => {
                        const el = document.createElement('div');
                        el.innerText = reaction;
                        el.style.position = 'absolute';
                        el.style.left = (Math.random() * 80 + 10) + '%';
                        el.style.bottom = '100px';
                        el.style.fontSize = (Math.random() * 20 + 20) + 'px';
                        el.style.pointerEvents = 'none';
                        el.style.zIndex = '100';
                        el.style.transition = 'all 2s ease-out';
                        el.style.opacity = '1';
                        el.style.transform = `translate(-50%, 0) scale(0.5)`;

                        container.appendChild(el);

                        requestAnimationFrame(() => {
                            el.style.bottom = (Math.random() * 200 + 300) + 'px';
                            el.style.opacity = '0';
                            el.style.transform = `translate(${Math.random() * 100 - 50}px, 0) scale(1.5)`;
                        });

                        setTimeout(() => el.remove(), 2000);
                    }, i * 100);
                }

                this.showNotification(`Reaction Sent: ${reaction}`, 'info');
            }
        }

        const app = new HoloStreamApp();
